import { makeStyles } from "@material-ui/core";

export const useProfileStyles = makeStyles(() => ({
    root: {
        width: "100%",
        display: "flex",
        flexDirection: "column",

        "& a": {
            textAlign: "center",
            cursor: "pointer"
        }
    },
    centeredContent: {
        width: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        paddingTop: "10px",
        "& h6": {
            fontWeight: "bold",
            padding: "0 0 10px"
        },
        "& p": {
            textAlign: "center",
            maxWidth: "320px",
            padding: "0 0 10px"
        }
    }
}));
