import React, { useState } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import ArrowLeft from "@cvccorp-components/icons/dist/ArrowLeft";
import DropDown from "../DropDown";
import Typography from "../Typography";
import Animate from "../Animate";
import { styles } from "./styles";

const SubmenuMobile = ({ classes, clickBack, menu, isOpen }) => {
    const getMenu = dataMenu =>
        dataMenu.map((item, key) => (
            <a key={key} href={item.link} target={item.target} className={classes.dropdownLink}>
                <Typography type="body2regular">
                    {item.icon} {item.label}
                </Typography>
            </a>
        ));

    const [isOpenAnimation, setOpenAnimation] = useState(isOpen);

    const onOpen = () => {
        setOpenAnimation(true);
    };
    const onClose = () => {
        setOpenAnimation(false);
    };

    return (
        <Animate isOpen={isOpen} onOpen={onOpen} onClose={onClose}>
            {isOpenAnimation && <span className={classes.overlay} />}
            <div className={isOpenAnimation ? classes.opened : classes.closed}>
                <div
                    onClick={() => {
                        clickBack(true);
                    }}
                >
                    <Typography type="body1regular" className={classes.header}>
                        <ArrowLeft size="small" className={classes.headerIcon} /> Mais
                    </Typography>
                </div>

                <DropDown className={classes.dropdown} menu={getMenu(menu || [])} />
            </div>
        </Animate>
    );
};

SubmenuMobile.propTypes = {
    classes: PropTypes.oneOfType([PropTypes.string, PropTypes.object]),
    menu: PropTypes.array,
    isOpen: PropTypes.bool,
};

export default withStyles(styles, { withTheme: true })(SubmenuMobile);
