export const styles = theme => ({
    container: {
        background: theme.palette.white.main,
        [theme.breakpoints.between("md", "xl")]: {
            borderRadius: "8px",
            boxShadow: theme.shadows[8],
        },
    },
    calendar: {
        overflow: "hidden",
        transition: "height ease-in-out .5s",
        zIndex: theme.level.modal,
        [theme.breakpoints.between("xs", "md")]: {
            background: theme.palette.white.main,
            bottom: 0,
            left: 0,
            height: "100%",
            overflow: "auto",
            position: "fixed",
            right: 0,
            top: 0,
        },
        [theme.breakpoints.between("md", "xl")]: {
            height: "400px",
            left: "-11px",
            padding: "0 20px",
            position: "absolute",
            top: "9px",
        },
    },
    closed: { height: 0 },
    header: {
        display: "flex",
        justifyContent: "space-between",
        padding: theme.spacing(2),
        [theme.breakpoints.between("xs", "md")]: {
            borderBottom: `1px solid ${theme.palette.grey[50]}`,
            flexDirection: "column",
        },
        [theme.breakpoints.between("md", "xl")]: { alignItems: "center" },
    },
    title: {
        color: theme.palette.text.primary,
        [theme.breakpoints.between("xs", "md")]: {
            alignItems: "center",
            display: "flex",
            paddingBottom: theme.spacing(2),
            position: "relative",
            "&:after": {
                borderTop: `1px solid ${theme.palette.grey[50]}`,
                bottom: 0,
                content: "\"\"",
                left: theme.spacing(2) * -1,
                position: "absolute",
                right: theme.spacing(2) * -1,
            },
        },
    },
    icon: { marginRight: theme.spacing(1) },
    dates: {
        display: "flex",
        [theme.breakpoints.between("xs", "md")]: { marginTop: theme.spacing(2) },
        [theme.breakpoints.between("md", "xl")]: { width: "calc(50% - 22px )" },
    },
    datesItem: {
        flex: 1,
        "& + &": { marginLeft: theme.spacing(2) },
    },
    dateInput: { padding: 0 },
    content: {
        [theme.breakpoints.between("xs", "md")]: {
            height: "calc(100% - 200px)",
            position: "absolute",
            width: "100%",
        },
    },
    footer: {
        background: theme.palette.white.main,
        boxShadow: theme.shadows[8],
        bottom: 0,
        opacity: 0,
        padding: theme.spacing(2),
        position: "fixed",
        transition: "opacity ease-in-out 2s",
        width: "100%",
        boxSizing: "border-box",
    },
    showFooter: { opacity: 1 },
    input: { fontSize: "14px" },
});
