import React, { useContext } from "react";
import { Typography } from "@material-ui/core";
import { BrandContext } from "../../../../BrandContext";
import Link from "../../../../Link";
import translate from "../../i18n/translate";
import SubTitle from "../SubTitle";

const Terms: React.FC = () => {
    const ctx = useContext(BrandContext);

    return (
        <div style={{
            textAlign: "left",
            width: "100%",
            marginTop: "8px",
            display: "flex",
            flexDirection: "column",
            flex: "1",
            alignItems: "baseline",
            justifyContent: "flex-end",

        }}>
            <div style={{ marginBottom: "3px" }}>
                <Typography color="textPrimary" variant="caption" >
                    {translate({ id: "MainScreen.termText" })}
                </Typography>
            </div>
            <div style={{ fontSize: "12px" }}>
                <Link target='_blank' href={ctx?.termConditionLink}>
                    <SubTitle variant="caption" style={{ fontWeight: 700, fontSize: "12px" }}>
                        {translate({ id: "MainScreen.termConditionLink" })}
                    </SubTitle>
                </Link>
                {translate({ id: "MainScreen.and" })}
                <Link target='_blank' href={ctx?.privacityLink}>
                    <SubTitle variant="caption" style={{ fontWeight: 700, fontSize: "12px" }}>
                        {translate({ id: "MainScreen.privacityLink" })}
                    </SubTitle>
                </Link>
            </div>
        </div>
    );
};

export default Terms;
