import React from "react";
import { Paper, Divider } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import { makeStyles, MuiThemeProvider } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import { storiesOf } from "@storybook/react";
import themes from "../../Theme/themes";
import { BrandType } from "../../types/brand";
import { ProductType } from "../../types/product";
import Card from "../Card";

const useStyles = makeStyles(theme => ({
    root: {
        background: theme.palette.background.default,
        boxShadow: theme.shadows[8],
        padding: "30px"
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px"
    }
}));

type CardStoryProps = {
    brand: BrandType;
    product: ProductType;
    title: string;
    subtitle: string;
    imgs: [];
    departure: string;
    arrival: string;
    totalDays: string;
    stars: number;
    price: string;
    taxesIncluded: boolean;
    parcel: string;
    small: boolean;
    isLoading: boolean;
    mode: string;
    shipmentTaxes: string;
    items: [];
};

const CardStory = ({ brand, product, title, subtitle, stars, price, taxesIncluded, parcel, imgs, small, isLoading, mode, shipmentTaxes, items }: CardStoryProps) => {
    const classes = useStyles(themes[brand]);

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} id="checkbox">
                <Typography variant="h3">Controles de seleção</Typography>
                <br />
                <Divider />
                <br />
                <Grid container spacing={5}>
                    <Grid item xs={small ? 2 : 3}>
                        <Card product={product} title={title} subtitle={subtitle} stars={stars} price={price} taxesIncluded={taxesIncluded} parcel={parcel} imgs={imgs} small={small} isLoading={isLoading} shipmentTaxes={shipmentTaxes} items={items} />
                    </Grid>
                </Grid>
            </Paper>
        </MuiThemeProvider>
    );
};

storiesOf("Components/Cards/CardPromo", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } }).add("Controls", args => <CardStory {...args} />, {
    args: {
        brand: "CVC",
        product: "package",
        title: "Item 2",
        subtitle: "Subtitle 2",
        imgs: ["http://lorempixel.com/output/city-q-c-298-285-3.jpg"],
        departure: "27 de ago 2019",
        arrival: "20 de ago 2019",
        totalDays: "7",
        stars: 4,
        price: "100",
        taxesIncluded: true,
        parcel: "12",
        small: false,
        isLoading: false,
        mode: "root",
        shipmentTaxes: 0, 
        items: [
            { icon: "person", order: 1, info: 5, code: "passagers" },
            { icon: "bag", order: 2, info: 2, code: "bag" },
            { icon: "weight", order: 3, info: 2, code: "weight" },
        ],
    },
    argTypes: {
        brand: {
            type: "select",
            options: ["BlackAndWhite", "BlackFriday", "CVC", "CVCCorp", "CVCYellow", "Sub"]
        },
        product: {
            options: ["package", "air", "hotel", "car"],
            control: { type: "select" }
        },
        title: { type: "string" },
        subtitle: { type: "string" },
        imgs: { type: "array" },
        departure: { type: "string" },
        arrival: { type: "string" },
        totalDays: { type: "string" },
        stars: { type: "number" },
        price: { type: "string" },
        taxesIncluded: { type: "boolean" },
        parcel: { type: "string" },
        small: { type: "boolean" },
        isLoading: { type: "boolean" },
        items: { type: "array"},
        shipmentTaxes : { type: "number" },
        mode: {
            type: "select",
            options: ["root", "darkBg"]
        }
    }
});
