export const styles = theme => ({
    root: {
        borderRadius: "100px",
        fontFamily: theme.font.semibold,
        fontSize: "12px",
        height: "24px",
        overflow: "hidden",
    },
    icon: {
        background: theme.palette.spot.main,
        borderRadius: "100%",
        color: theme.palette.white.main,
        margin: 0,
    },
    label: {
        padding: `0 ${theme.spacing(1)}px`,
        "&:empty": { display: "none" },
    },
    spot: {
        background: `${theme.palette.spot.main}`,
        color: `${theme.palette.white.main}`,
    },
    white: {
        background: `${theme.palette.white.main}`,
        boxShadow: `${theme.shadows[24]}`,
    },
    hotel: {
        background: `${theme.palette.hotel.main}`,
        color: `${theme.palette.white.main}`,
    },
    air: {
        background: `${theme.palette.air.main}`,
        color: `${theme.palette.white.main}`,
    },
    package: {
        background: `${theme.palette.package.main}`,
        color: `${theme.palette.white.main}`,
    },   
    car: {
        background: `${theme.palette.car.main}`,
        color: `${theme.palette.white.main}`,
    },
    canceled: {
        background: `${theme.palette.canceled.main}`,
        color: `${theme.palette.white.main}`,
    },
    processed: {
        background: `${theme.palette.processed.main}`,
        color: `${theme.palette.white.main}`,
    },
    approved: {
        background: `${theme.palette.approved.main}`,
        color: `${theme.palette.white.main}`,
    },
    orange: {
        background: "#FF9D00",
        color: `${theme.palette.white.main}`,
    },
    noIcon: {
        minWidth: "70px",
        padding: `0 ${theme.spacing(2)}px`,
    },
    hasIcon: {
        paddingLeft: 0,
        "&:empty": { display: "none" },
    },
    hasIconLabel: {
        paddingLeft: `${theme.spacing(0.5)}px`,
        "&:empty": { display: "none" },
    },
});
