import { makeStyles, Theme } from "@material-ui/core/styles";

const useStyles = makeStyles((theme: Theme) => ({
    root: {
        width: "100%",
        height: "100%"

    },
    btnLogin: {
        width: "100%",
        margin: `${theme.spacing(2)}px 0px`,
    },
    inputWrapper: {
        margin: '16px 0 5px 0',
        width: '100%'
    },
    orSeparateWrapper: {
        position: "relative",
        width: '100%',
        margin: '7px 0',
    },
    orSeparate: {
        fontSize: '12px',
        lineHeight: '150%',
        fontWeight: 'bold',
        color: '#454848',
        zIndex: 9,

        "&::after": {
            content: "''",
            width: '47%',
            height: '1px',
            display: "block",
            backgroundColor: '#CACACA',
            position: 'absolute',
            top: 10
        },
        "&::before": {
            content: "''",
            width: '47%',
            height: '1px',
            display: "block",
            backgroundColor: '#CACACA',
            position: 'absolute',
            top: 10,
            right: 0
        }
    }

}));
export default useStyles;
