/* eslint-disable @typescript-eslint/no-empty-function */
// eslint-disable-next-line @typescript-eslint/no-empty-function
import React, { useContext } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import LazyLoad from "react-lazyload";
import Slider from "react-slick";
import Meal from "@cvccorp-components/icons/dist/Meal";
import Favorite from "@cvccorp-components/icons/dist/Favorite";
import Air from "@cvccorp-components/icons/dist/Air";
import Luggage from "@cvccorp-components/icons/dist/Luggage";
import DropLeft from "@cvccorp-components/icons/dist/DropLeft";
import DropRight from "@cvccorp-components/icons/dist/DropRight";
import "slick-carousel/slick/slick.css";
import Tag from "../../Tag";
import LoadingContent from "../../LoadingContent";
import Typography from "../../Typography";
import Box from "../../Box";
import Stars from "../../Stars";
import Price from "../../Price";
import TripAdvisor from "../../TripAdvisor";
import "../../assets/css/slick-theme.css";

import { styles } from "./styles";
import { BrandContext } from "../../BrandContext/BrandContext";

const ResultItemPackage = ({
    classes,
    className,
    cloudinarySettings,
    onClick,
    isLoading,
    type,
    imgs,
    notAvaliable,
    meal,
    onFav,
    title,
    stars,
    subtitle,
    rate,
    totalAvaliations,
    discount,
    stops,
    departure,
    arrival,
    luggage,
    totalPrice,
    partialPrice,
    totalPeople,
    totalDays,
    taxesIncluded,
    parcel,
    parcelInfo,
    isFavorite,
    installmentRoundedValueWithTax,
    isActive,
    id,
    lastItem
}) => {
    const NextArrow = props => {
        const handleClick = e => {
            if (props.onClick) props.onClick(e);
            e.stopPropagation();
        };
        return (
            <button type="button" className={clsx(classes.arrows, classes.arrowRight)} onClick={e => handleClick(e)}>
                <DropRight size="medium" />
            </button>
        );
    };

    const PrevArrow = props => {
        const handleClick = e => {
            if (props.onClick) props.onClick(e);
            e.stopPropagation();
        };
        return (
            <button type="button" className={clsx(classes.arrows, classes.arrowLeft)} onClick={e => handleClick(e)}>
                <DropLeft size="medium" />
            </button>
        );
    };

    const classNameBox = () => {
        if (type === "list") {
            if (isLoading) {
                return clsx(classes.root, classes.rootList, classes.rootLoading, className);
            }
            return clsx(classes.root, classes.rootList, className, isActive && classes.isActive);
        }
        return clsx(classes.root, classes.package, className);
    };

    const brandCtx = useContext(BrandContext);
    return (
        <Box className={classNameBox()} onClick={onClick} id={id}>
            {!isLoading && (
                <div className={type === "list" ? classes.typeList : ""}>
                    {imgs && (
                        <div className={type === "card" ? classes.imgContainerCard : classes.imgContainerList}>
                            {imgs.length > 1 && <span className={clsx(classes.overlay, "overlay")} />}
                            <Slider infinite={false} speed={500} slidesToShow={1} touchThreshold={100} dots nextArrow={<NextArrow />} prevArrow={<PrevArrow />}>
                                {imgs.map((item, i) => (
                                    <span className={type === "list" ? clsx(classes.imgWrapper, classes.imgWrapperList) : classes.imgWrapper} key={i}>
                                        <LazyLoad height={229} placeholder={<LoadingContent />}>
                                            <img className={notAvaliable ? clsx(classes.notAvaliableImg, classes.img) : classes.img} src={`${brandCtx.imageProcessorPath}image/fetch/${cloudinarySettings}/${item}`} alt="" />
                                        </LazyLoad>
                                    </span>
                                ))}
                            </Slider>
                        </div>
                    )}
                    {meal && !notAvaliable && <Tag className={imgs ? classes.topTag : ""} label={meal} icon={<Meal size="medium" className={classes.mealIcon} />} color="white" />}
                    {notAvaliable && <Tag className={imgs ? classes.topTag : ""} label="Hotel indisponível para estas datas" color="white" />}
                    {onFav && (
                        <span className={type === "list" ? clsx(classes.favorite, classes.favoriteList) : classes.favorite}>
                            <Favorite fill={isFavorite} size="medium" onClick={onFav} />
                        </span>
                    )}
                    <div className={type === "list" ? classes.content : ""}>
                        <div className={classes.header}>
                            <Typography className={type === "card" ? classes.title : "title"} type="headline6">
                                {title}
                            </Typography>
                            <Typography className={classes.subtitle} type="body2regular">
                                {stars && <Stars stars={stars} size="small" className={clsx("hotel-stars", classes.stars)} />}
                                {subtitle}
                            </Typography>
                        </div>
                        <div className={classes.info}>
                            {rate ? (
                                <TripAdvisor className={type === "list" ? classes.tripAdvisorList : classes.tripAdvisor} totalAvaliations={totalAvaliations} rate={rate} />
                            ) : (
                                <span className={type === "list" ? classes.tripAdvisorList : classes.tripAdvisor} />
                            )}
                            {discount && <Tag className={type === "list" ? classes.discountList : classes.discountCard} label={discount} color="package" />}
                            {lastItem && <Tag className={type === "card" && classes.discountCard} label={lastItem} color="canceled" />}
                        </div>
                        <Grid container alignItems="center">
                            <Grid item xs={7}>
                                <div className={classes.flight}>
                                    <span>
                                        <Typography type="captionsemibold" className={classes.vooLabel}>
                                            Voo sugerido{" "}
                                        </Typography>
                                        <Typography type="captionregular">{stops ? "com" : "sem"} paradas</Typography>
                                    </span>
                                    <Typography type="body1semibold" className={classes.flightInfo}>
                                        {departure}
                                        <Air />
                                        {arrival}
                                        <span className={classes.luggage}>
                                            <Luggage {...luggage} />
                                        </span>
                                    </Typography>
                                </div>
                            </Grid>
                            {!notAvaliable && (
                                <Grid item xs={5}>
                                    <Price className={classes.priceContainer} product="package" totalPrice={totalPrice} partialPrice={partialPrice} totalPeople={totalPeople} totalDays={totalDays} />
                                </Grid>
                            )}
                        </Grid>
                        {(taxesIncluded || parcel || parcelInfo) && (
                            <div className={classes.footer}>
                                {taxesIncluded && (
                                    <Typography className={classes.footerItem} type="captionregular">
                                        Taxas inclusas
                                    </Typography>
                                )}
                                {parcel && (
                                    <Typography className={classes.footerItem} type="captionsemibold">
                                        Em até {parcel}x de <span className={classes.vooLabel}>R${installmentRoundedValueWithTax}</span>
                                    </Typography>
                                )}
                                {parcelInfo && !parcel && (
                                    <Typography className={classes.footerItem} type="captionsemibold">
                                        {parcelInfo}
                                    </Typography>
                                )}
                            </div>
                        )}
                    </div>
                </div>
            )}
            {isLoading && (
                <>
                    <Grid container spacing={2} className={type === "list" && classes.loading}>
                        <Grid item xs={type === "list" ? 6 : 12} className={classes.relative}>
                            <div className={type === "card" ? classes.imgContainerCard : ""}>
                                <div className={classes.imgContainerPromoSmall}>
                                    <LoadingContent absolute />
                                </div>
                            </div>
                        </Grid>
                        <Grid item xs={type === "list" ? 6 : 12}>
                            <Grid container>
                                <Grid item xs={10}>
                                    <LoadingContent />
                                </Grid>
                            </Grid>
                            <Grid container className={classes.loadingContent}>
                                <Grid item xs={5}>
                                    <LoadingContent />
                                </Grid>
                            </Grid>
                            <Grid container className={classes.loadingContent}>
                                <Grid item xs={9}>
                                    <LoadingContent />
                                </Grid>
                            </Grid>
                            <Grid container className={classes.loadingPrice}>
                                <Grid item xs={3}>
                                    <LoadingContent />
                                </Grid>
                            </Grid>
                        </Grid>
                    </Grid>
                </>
            )}
        </Box>
    );
};

ResultItemPackage.defaultProps = {
    type: "list",
    imgs: [],
    isLoading: false,
    notAvaliable: false,
    isFavorite: false,
    taxesIncluded: false,
    onFav: undefined,
    cloudinarySettings: "",
    onClick: () => {}
};
ResultItemPackage.defaultProps = {
    luggage: { carryOn: true, handBag: false, checkedBag: false }
};
ResultItemPackage.prototype = {
    type: PropTypes.string,
    imgs: PropTypes.array,
    isLoading: PropTypes.bool,
    onClick: PropTypes.func,
    title: PropTypes.string,
    subtitle: PropTypes.string,
    cloudinarySettings: PropTypes.string,
    meal: PropTypes.string,
    notAvaliable: PropTypes.bool,
    isFavorite: PropTypes.bool,
    rate: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
    onFav: PropTypes.func,
    stars: PropTypes.number,
    totalDays: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
    partialPrice: PropTypes.string,
    totalPrice: PropTypes.string,
    amenities: PropTypes.array,
    taxesIncluded: PropTypes.bool,
    parcel: PropTypes.string,
    parcelInfo: PropTypes.string,
    totalAvaliations: PropTypes.string,
    discount: PropTypes.string,
    departure: PropTypes.string,
    arrival: PropTypes.string,
    luggage: PropTypes.object,
    stops: PropTypes.bool,
    lastItem: PropTypes.string
};

export default withStyles(styles, { withTheme: true })(ResultItemPackage);
