export const styles = theme => ({
    root: {
        border: "1px solid transparent",
        cursor: "pointer",
        overflow: "hidden",
        padding: 0,
    },
    img: {
        display: "block",
        left: "50%",
        minWidth: "100%",
        position: "absolute",
        transform: "translate(-50%, -50%)",
        top: "50%",
    },
    content: {
        alignItems: "center",
        display: "flex",
        justifyContent: "space-between",
        padding: theme.spacing(2),
    },
    info: {
        width: "60%",
    },
    title: {
        display: "block",
        maxWidth: "100%",
        overflow: "hidden",
        textOverflow: "ellipsis",
        whiteSpace: "nowrap",
    },
    imgContainer: {
        background: theme.palette.backdrop.main,
        display: "block",
        height: "185px",
        overflow: "hidden",
        position: "relative",
    },
    tagContainer: { position: "relative" },
    tag: {
        bottom: "-12px",
        position: "absolute",
        right: theme.spacing(2),
    },
    subtitle: {
        display: "block",
        margin: `${theme.spacing(1) / 2}px 0`,
    },
    topTag: {
        left: theme.spacing(2),
        position: "absolute",
        top: theme.spacing(2),
    },
    mealIcon: {
        padding: theme.spacing(0.5),
    },
    fav: {
        color: theme.palette.white.main,
        cursor: "pointer",
        position: "absolute",
        right: theme.spacing(2),
        top: theme.spacing(2),
    },
    stars: {
        color: theme.palette.secondary.main,
        marginRight: theme.spacing(0.5),
    },
    notAvaliableImg: { filter: "grayscale(1)" },
    priceContainer: {
        textAlign: "right",
        margin: "-10px 0"
    },
});
