import React, { useState } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import Delete from "@cvccorp-components/icons/dist/Delete";
import DropDown from "@cvccorp-components/icons/dist/DropDown";
import FilterIcon from "@cvccorp-components/icons/dist/Filter";
import Typography from "../Typography";
import Button from "../Button";
import FilterTitle from "../FilterTitle";
import Overlay from "../Overlay";
import Animate from "../Animate";
import { styles } from "./styles";

const Filter = ({ label, isOpen, isActive, sufix, prefix, onOpen, onApply, onReset, onClose, type, items, classes, className, value, disabled, noModal, more, isMobile, header, height, qtdFilters }) => {
    const handleOpen = e => {
        e.preventDefault();
        e.stopPropagation();
        if (onOpen) onOpen();
    };
    const handleClose = e => {
        e.preventDefault();
        e.stopPropagation();
        if (onClose) onClose();
    };

    const handleApply = e => {
        e.preventDefault();
        e.stopPropagation();
        if (onApply) onApply();
    };

    const handleReset = e => {
        e.preventDefault();
        e.stopPropagation();
        if (onReset) onReset();
    };

    const [isOpenAnimation, setOpenAnimation] = useState(false);

    const onOpenAnimate = () => {
        setOpenAnimation(true);
    };
    const onCloseAnimate = () => {
        setOpenAnimation(false);
    };

    return (
        <div
            className={`${classes.root} ${className || ""} ${isActive ? classes.active : ""} ${noModal ? classes.noModal : ""} ${noModal && more && isMobile ? classes.noModalMore : ""} ${isOpen && !isMobile && !noModal ? classes.opened : ""
            }`}
            onClick={e => {
                if (!noModal) handleOpen(e);
            }}
        >
            {noModal && isMobile && more && <div className={classes.header}>{header}</div>}
            {isActive && !noModal && (
                <>
                    <Typography type="subtitle" className={classes.title}>
                        {value.length > 1 ? `${value.length} ${label}` : ` ${prefix} ${value[0]} ${sufix}`}
                    </Typography>
                    <button className={classes.buttonDelete} onClick={handleReset}>
                        <Delete className={classes.delete} size="small" />
                    </button>
                </>
            )}
            {!isActive && !noModal && (
                <>
                    <Typography type="subtitle" className={classes.title}>
                        {label}
                    </Typography>
                    <DropDown size="small" className={isOpen ? classes.iconOpened : classes.iconClosed} />
                </>
            )}
            {noModal && more && (
                <Typography type="body1regular" className={classes.title}>
                    <FilterIcon /> {label}
                    <Button className={classes.btnReset} color="primary" label="Limpar filtros" variant="text" disabled={disabled} onClick={e => handleReset(e)} />
                </Typography>
            )}
            {noModal && !more && <FilterTitle label={label} />}
            {noModal ? (
                <Content {...{ classes, isOpenAnimation, isMobile, noModal, height, items, qtdFilters, handleReset, handleApply, disabled, more, type, isOpen }} />
            ) : (
                <Animate isOpen={isOpen} onOpen={onOpenAnimate} onClose={onCloseAnimate}>
                    {!isMobile && <Overlay onClick={e => handleClose(e)} />}
                    <Content {...{ classes, isOpenAnimation, isMobile, noModal, height, items, qtdFilters, handleReset, handleApply, disabled, more, type, isOpen }} />
                </Animate>
            )}
        </div>
    );
};

Filter.propTypes = {
    classes: PropTypes.object.isRequired,
    className: PropTypes.object,
    label: PropTypes.string.isRequired,
    items: PropTypes.array.isRequired,
    isOpen: PropTypes.bool,
    isActive: PropTypes.bool,
    type: PropTypes.string,
    onOpen: PropTypes.func,
    onApply: PropTypes.func,
    onReset: PropTypes.func,
    onClose: PropTypes.func,
    value: PropTypes.array,
    noModal: PropTypes.bool,
    prefix: PropTypes.string,
    sufix: PropTypes.string
};

Filter.defaultProps = {
    type: "list",
    isOpen: false,
    prefix: "",
    sufix: "",
    height: "auto"
};

const Content = ({ classes, isOpenAnimation, isMobile, noModal, height, items, qtdFilters, handleReset, handleApply, disabled, more, type, isOpen }) => {
    return (
        <div
            className={`${classes.container} ${isOpenAnimation && !isMobile && !noModal ? classes.open : ""}
      ${!noModal && classes[type]}
      ${noModal ? classes.noModalContainer : ""} ${noModal && more && isMobile ? classes.noModalContainerMobile : ""}`}
        >
            {type == "list" && (
                <ul id="listMore" className={more ? classes.contentMore : classes.content} style={{ maxHeight: height }}>
                    {items &&
                        items.map((item, i) => (
                            <li key={i} className={classes.contentItem}>
                                <span className={classes.item}>{item}</span>
                            </li>
                        ))}
                </ul>
            )}
            {type == "grid" && (
                <Grid container spacing={1} className={classes.content} style={{ maxHeight: height }}>
                    {items &&
                        items.map((item, i) => (
                            <Grid item xs={items.length === 2 || noModal ? 6 : 4} key={i}>
                                {item}
                            </Grid>
                        ))}
                </Grid>
            )}
            {!noModal && (
                <footer className={classes.footer}>
                    <Button className={classes.btn} color="primary" label="Limpar" variant="text" disabled={disabled} onClick={e => handleReset(e)} />
                    <Button className={classes.btnApply} color="primary" label="Aplicar" variant="text" disabled={disabled} onClick={e => handleApply(e)} />
                </footer>
            )}
            {noModal && more && isMobile && (
                <footer className={`${classes.footer} ${classes.footerMobile} ${isOpen && classes.showFooterMobile}`}>
                    <Button
                        className={classes.btnApply}
                        color="secondary"
                        label={qtdFilters > 0 ? (qtdFilters > 1 ? `Aplicar ${qtdFilters} Filtros` : `Aplicar ${qtdFilters} Filtro`) : "Aplicar"}
                        variant="contained"
                        disabled={disabled}
                        onClick={e => handleApply(e)}
                        fullWidth
                    />
                </footer>
            )}
        </div>
    );
};

export default withStyles(styles, { withTheme: true })(Filter);
