export const styles = theme => ({
    root: {
        border: "2px solid transparent",
        borderRadius: theme.spacing(2),
        boxShadow: theme.shadows[16],
        cursor: "pointer",
        overflow: "hidden",
        padding: theme.spacing(2),
        position: "relative",
        "& button": {
            display: "none",
        },
        "&:hover": {
            borderColor: theme.palette.spot
                ? theme.palette.hotel.main
                : theme.palette.primary.main,

            "& .title": {
                color: theme.palette.hotel.main,
            },
            "& button": {
                display: "block",
            },
        },
    },
    rootLoading: {
        boxShadow: theme.shadows[0],
        "&:hover": { borderColor: "transparent" },
    },
    isActive: {
        borderColor: theme.palette.spot
            ? theme.palette.hotel.main
            : theme.palette.primary.main,
    },
    loading: {
        cursor: "default",
        height: "244px",
        padding: theme.spacing(1),
    },
    relative: {
        borderRadius: theme.spacing(2),
        overflow: "hidden",
        position: "relative",
    },
    package: {
        "&:hover": {
            borderColor: theme.palette.package
                ? theme.palette.package.main
                : theme.palette.primary.main,
        },
    },
    rootList: { padding: theme.spacing(1) },
    typeList: { display: "flex" },
    content: {
        display: "flex",
        flexDirection: "column",
        padding: `0 ${theme.spacing(3)}px`,
        position: "relative",
        width: "50%",
    },
    img: {
        display: "block",
        left: "50%",
        maxWidth: "110%",
        minHeight: "100%",
        minWidth: "100%",
        position: "absolute",
        transform: "translate(-50%, -50%)",
        top: "50%",
    },
    arrows: {
        background: "none",
        border: "none",
        color: theme.palette.white.main,
        cursor: "pointer",
        outline: "none",
        padding: 0,
        position: "absolute",
        transform: "translateY(-50%)",
        top: "50%",
        zIndex: theme.level.content,
    },
    arrowLeft: {
        left: theme.spacing(1),
    },
    arrowRight: {
        right: theme.spacing(1),
    },
    mealIcon: {
        boxSizing: "border-box",
        padding: "3px",
    },
    title: {
        display: "block",
        marginTop: theme.spacing(3),
    },
    footer: {
        borderTop: `1px solid ${theme.palette.grey[50]}`,
        bottom: 0,
        padding: `${theme.spacing(1.2)}px 0 0`,
        textAlign: "center",
        [theme.breakpoints.between("md", "xl")]: {
            left: 0,
            position: "absolute",
            right: theme.spacing(2) * -1,
        },
        [theme.breakpoints.between("xs", "md")]: {
            marginTop: theme.spacing(2),
            marginLeft: theme.spacing(2) * -1,
            marginRight: theme.spacing(2) * -1,
        },
    },
    footerItem: {
        "& + &": {
            marginLeft: theme.spacing(1) / 2,
            "&:before": {
                borderLeft: `1px solid ${theme.palette.text.primary}`,
                content: "\"\"",
                display: "inline-block",
                height: "10px",
                marginRight: theme.spacing(1) / 2,
                verticalAlign: "middle",
            },
        },
    },
    priceContainer: { textAlign: "right" },
    imgContainerCard: {
        display: "block",
        height: "185px",
        marginLeft: theme.spacing(2) * -1,
        marginTop: theme.spacing(2) * -1,
        position: "sticky",
        width: `calc(100% + ${theme.spacing(4)}px)`,
        borderRadius: `${theme.spacing(2)} ${theme.spacing(2)} 0 0`,
        overflow: "hidden",
        "& .slick-list": {
            borderRadius: `${theme.spacing(2)}px`,
            "-webkit-border-radius" :`${theme.spacing(2)}px ${theme.spacing(2)}px 0 0`
        }
    },
    imgContainerList: {
        height: "224px",
        maxWidth: "50%",
        minWidth: "50%",
        position: "relative",
        width: "50%",
        "& .slick-list": {
            borderRadius: `${theme.spacing(2)}px`,
            "-webkit-border-radius" :`${theme.spacing(2)}px ${theme.spacing(2)}px 0 0`
        }
    },
    imgWrapper: {
        background: theme.palette.backdrop
            ? theme.palette.backdrop.main
            : theme.palette.primary.main,
        display: "block",
        height: "185px",
        overflow: "hidden",
        position: "relative",
    },
    imgWrapperList: {
        borderRadius: theme.spacing(2),
        "-webkit-border-radius": `${theme.spacing(2)}px`,        
        height: "224px",
    },
    overlay: {
        background: theme.palette.gradient
            ? theme.palette.gradient.black.bottom
            : "",
        borderRadius: theme.spacing(2),
        "-webkit-border-radius" : `${theme.spacing(2)}px`,
        bottom: 0,
        content: "\"\"",
        left: 0,
        position: "absolute",
        right: 0,
        top: 0,
        zIndex: theme.level.content,
    },
    notAvaliableImg: { filter: "grayscale(1)" },
    subtitle: {
        display: "block",
        margin: `${theme.spacing(1) / 2}px 0`,
    },
    loadingContent: { marginTop: theme.spacing(2) },
    loadingPrice: { marginTop: theme.spacing(5) },
    topTag: {
        left: theme.spacing(2),
        position: "absolute",
        top: theme.spacing(2),
        zIndex: theme.level.content + 1,
    },
    favorite: {
        color: theme.palette.white
            ? theme.palette.white.main
            : theme.palette.primary.main,
        cursor: "pointer",
        position: "absolute",
        right: theme.spacing(2),
        top: theme.spacing(2),
    },
    favoriteList: { color: theme.palette.text.primary },
    stars: {
        color: theme.palette.secondary.main,
        marginRight: theme.spacing(0.5),
    },
    tripAdvisor: {
        margin: `${theme.spacing(2)}px 0`,
        width: "100%",
    },
    tripAdvisorList: {
        margin: `${theme.spacing(2)}px 0`,
        minHeight: "36px",
        width: "50%",
    },
    discountCard: {
        position: "absolute",
        right: "16px",
        top: "175px",
    },
    discountList: { width: "70px" },
    info: {
        alignItems: "center",
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between",
    },
    flight: {
        display: "flex",
        flexDirection: "column",
    },
    flightInfo: {
        alignItems: "center",
        display: "flex",
        justifyContent: "space-between",
        width: "150px",
    },
    luggage: { color: theme.palette.text.primary },
    vooLabel: { color: `${theme.palette.package.main} !important` },
});
