import { makeStyles } from "@material-ui/core/styles";

export const useStyles = makeStyles(theme => ({
    avatar: {
        background: `${theme.palette.primary.dark}`,
        borderRadius: "8px",
        color: `${theme.palette.white.main}`,
        fontSize: "13px",
        height: "32px",
        textTransform: "uppercase",
        width: "32px",
    },
    avatarLight: {
        background: `${theme.palette.primary.dark}`,
        borderRadius: "16px",
        color: `${theme.palette.white.main}`,
        fontSize: "14px",
        height: "48px",
        textTransform: "uppercase",
        width: "48px",
    },
    logged: {
        alignItems: "flex-end",
        display: "flex",
        justifyContent: "flex-end",
        padding: `${theme.spacing(3.7)}px 0`,
        flexDirection: "column",
    },
    loggedLight: {
        alignItems: "center",
        display: "flex",
        justifyContent: "space-between",
    },
    user: {
        [theme.breakpoints.up("md")]: {
            color: theme.palette.white.main,
        }
    },
    userLight: { display: "block" },
    logout: {
        minWidth: "100px",
        marginRight: 15,
        [theme.breakpoints.up("md")]: {
            background: "transparent",
            border: "none",
            color: theme.palette.white.main,
            padding: "0px",
            justifyContent: "end",
            margin: "0px",
            height: "auto",
        },
        "&:hover": {
            background: "transparent",
            border: "none",

        }
    },
    logoutLeft: {
        marginRight: 15
    },
    login: {
        padding: `${theme.spacing(4.5)}px 0`,
        textAlign: "right",
    },
    registerBtn: {
        marginRight: theme.spacing(3),
        textDecoration: "none",
    },
    registerBtnTxt: { color: `${theme.palette.secondary.main}` },
    loginBtn: {
        textDecoration: "none",
        cursor: "pointer",
        width: "auto!important"
    },
    loginBtnTxt: { color: `${theme.palette.white.main}` },
    loginNoRegister: {
        display: "flex",
        justifyContent: "flex-end",
        alignItems: "center"
    },
    hello: { display: "block" },
    iconLeft: {
        marginRight: 16,
        width: 36,
        height: 36,
        border: `1px solid ${theme.palette.common.white}`
    },
    iconRight: {},
    loginNewBtn: {
        padding: 0,
        display: "flex",
        alignItems: "center",
        justifyContent: "flex-end"
    },
    linkLogout: {
        position: "absolute",
        top: 26,
        right: 36,
    },
    link: {
        color: "inherit",
        textDecoration: "none",
        "&:hover": {
            textDecoration: "none"
        }
    },
    newButtonsContainer: {
        height: "100%",
        padding: 0,
        display: "flex",
        alignItems: "center",
        justifyContent: "flex-end"
    },
    linkMobile: {
        display: "block",
        padding: "7px 25px 8px",
        color: theme.palette.common.black,
        fontSize: "16px!important"
    },
    newButtonsContainerMobile: {
        height: "auto"
    },
    userInfo: {
        color: theme.palette.common.black,
        padding: "13px 25px 16px",
        display: "block"
    },
    userInfoMobile: {
        paddingBottom: 0
    },
    menu: {
        borderRadius: 8
    }
}));
