export const styles = theme => ({
    root: {
        [theme.breakpoints.between("md", "xl")]: {
            minHeight: "332px",
            position: "relative",
        },
        [theme.breakpoints.between("xs", "md")]: {
            padding: theme.spacing(2),
        },
    },
    title: {
        [theme.breakpoints.between("md", "xl")]: { display: "block" },
    },
    grid: {
        alignItems: "center",
        margin: "4px 6px 4px 6px",
        width: "calc(100% - 8px)",
        [theme.breakpoints.between("xs", "md")]: {
            margin: `${theme.spacing(1)}px 0`,
        },
    },
    text: { color: theme.palette.text.secondary },
    link: { color: theme.palette.primary.main },
    icon: {
        color: theme.palette.success.main,
        [theme.breakpoints.between("md", "xl")]: {
            left: "48px",
            position: "absolute",
        },
    },
    successBox: {
        [theme.breakpoints.between("md", "xl")]: { padding: "43px 104px" },
    },
    gridTitle: {
        padding: "10px 4px 20px 4px !important",
    },
    gridData: {
        width: "calc(100% - 8px)",
        margin: 0,
    },
    gridInfo: {
        padding: "24px 32px 24px 10px",
        margin: 0,
    },
});
