import React, { useState } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";

import ListItem from "@material-ui/core/ListItem";
import InputBase from "@material-ui/core/InputBase";
import List from "@material-ui/core/List";

import Air from "@cvccorp-components/icons/dist/Air";
import Hotel from "@cvccorp-components/icons/dist/Hotel";
import Local from "@cvccorp-components/icons/dist/Local";
import Clock from "@cvccorp-components/icons/dist/Clock";
import ArrowLeft from "@cvccorp-components/icons/dist/ArrowLeft";

import Typography from "../../Typography";
import Box from "../../Box";
import TextField from "../../TextField";
import Overlay from "../../Overlay";

import { styles } from "../styles";
import CloseIcon from "@material-ui/icons/Close";

const PesqincPackage = props => {
    const {
        classes,
        onChange,
        onFocus,
        onClose,
        onSelect,
        value,
        iata,
        selectedIndex,
        label,
        alignRight,
        resetValue
    } = props;

    const [index, setIndex] = useState(selectedIndex || 0);
    const [searching, setSearching] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);

    const handleSelect = (item, i) => {
        setSearching(false);
        setSelectedItem(item);
        onSelect(item, i);
    };

    const handleKeyUp = e => {
        if (props.search && props.search.length > 0) {
            if (e.keyCode === 40) setIndex((index + 1) % props.search.length);
            else if (e.keyCode === 38)
                setIndex((index - 1 + props.search.length) % props.search.length);
            else if (e.keyCode === 13 && searching) onSelect(props.search[index]);
        }
    };

    const handleChange = e => {
        if (onChange) onChange(e);
    };

    const handleFocus = e => {
        setSearching(true);
        if (onFocus) onFocus(e);
    };

    const handleClose = () => {
        if (!selectedItem || value !== selectedItem.name) {
            setSelectedItem(null);
        }

        setSearching(false);
        if (onClose) onClose(selectedItem);
    };

    const focusInputField = input => {
        if (searching && input) input.focus();
    };

    const handleReset = () => {
        resetValue();
    };

    return (
        <div className={classes.container}>
            <Typography
                type="headline5"
                className={alignRight ? classes.labelIataRight : classes.labelIata}
            >
                {iata}
            </Typography>
            <TextField
                label={label || "Para onde vai?"}
                size="large"
                transparent
                value={value}
                onClick={handleFocus}
                onChange={handleChange}
                onKeyUp={handleKeyUp}
                inputClassName={alignRight ? classes.inputPkgRight : classes.inputPkg}
                labelClassName={
                    alignRight ? classes.inputLabelRight : classes.inputLabel
                }
                error={props.error}
                errorMsg={props.errorMsg}
                disabled
                noBorder
            />

            {searching && handleClose && <Overlay onClick={handleClose} />}

            <Box
                className={`${classes.root} ${classes.rootPkg} ${!searching &&
          classes.closed}`}
            >
                <div className={classes.internalInput}>
                    {props.isMobile ? (
                        <a onClick={handleClose}>
                            <ArrowLeft />
                        </a>
                    ) : (
                        <Local />
                    )}
                    <InputBase
                        placeholder={label || "Para onde vai?"}
                        value={value}
                        onFocus={handleFocus}
                        onChange={handleChange}
                        onKeyUp={e => handleKeyUp(e, value)}
                        className={classes.flexGrow}
                        inputRef={focusInputField}
                    />
                    <CloseIcon
                        onClick={handleReset}
                    />
                </div>
                <div className={classes.boxResult}>
                    {!props.history &&
            (!props.search || props.search.length === 0) &&
            !props.mostSearched && (
                        <Typography
                            type="captionsemibold"
                            className={classes.listItemTitle}
                        >
                Continue digitando…
                        </Typography>
                    )}
                    <List component="nav" className={classes.list}>
                        {props.search && props.search.length > 0 && (
                            <li className={classes.listItemTitle}>
                                <Typography type="captionsemibold">
                  Você está buscando por…
                                </Typography>
                            </li>
                        )}
                        <div
                            className={`${!props.isMobile && classes.heightLimit} ${
                                classes.scrolling
                            }`}
                        >
                            {props.search &&
                props.search.map((item, i) => (
                    <>
                        <ListItem
                            key={i}
                            className={classes.listItem}
                            button
                            selected={i === index}
                            onClick={() => handleSelect(item, i)}
                        >
                            {item.type === "hotel" && <Hotel size="small" />}
                            {item.type === "location" && <Local size="small" />}
                            {item.type === "airport" && <Air size="small" />}
                            <span className={classes.content}>
                                <Typography
                                    className={classes.name}
                                    type="body2semibold"
                                >
                                    {item.name}
                                </Typography>
                                <Typography
                                    className={classes.adress}
                                    type="body2regular"
                                >
                                    {" "}
                                    {item.address}
                                </Typography>
                            </span>
                        </ListItem>
                        {item.near &&
                      item.near.map((subitem, i) => (
                          <ListItem
                              key={i}
                              className={`${classes.listItem} ${classes.sublistItem}`}
                              button
                              onClick={() => handleSelect(subitem, i)}
                          >
                              {subitem.type === "hotel" && <Hotel size="small" />}
                              {subitem.type === "location" && (
                                  <Local size="small" />
                              )}
                              {subitem.type === "airport" && <Air size="small" />}
                              <span className={classes.content}>
                                  <Typography
                                      className={classes.name}
                                      type="body2semibold"
                                  >
                                      {subitem.name}
                                      <span>{subitem.name}</span>
                                  </Typography>
                                  <Typography
                                      className={classes.adress}
                                      type="body2regular"
                                  >
                                      {" "}
                                      {subitem.address}
                                  </Typography>
                              </span>
                          </ListItem>
                      ))}
                    </>
                ))}
                        </div>
                        {props.history && !(props.search && props.search.length > 0) && (
                            <li className={classes.listItemTitle}>
                                <Typography type="captionsemibold">Últimas buscas</Typography>
                            </li>
                        )}
                        {props.history &&
              !(props.search && props.search.length > 0) &&
              props.history.map((item, i) => (
                  <ListItem
                      key={i}
                      className={classes.listItem}
                      button
                      onClick={() => handleSelect(item, i)}
                  >
                      <Clock size="small" />
                      <span className={classes.content}>
                          <Typography className={classes.name} type="body2semibold">
                              {item.name}
                          </Typography>

                          <Typography className={classes.adress} type="body2regular">
                              {" "}
                              {item.address}
                          </Typography>
                      </span>
                  </ListItem>
              ))}
                        {props.mostSearched && !(props.search && props.search.length > 0) && (
                            <li className={classes.listItemTitle}>
                                <Typography type="captionsemibold">
                  Destinos mais buscados
                                </Typography>
                            </li>
                        )}
                        {props.mostSearched &&
              !(props.search && props.search.length > 0) &&
              props.mostSearched.map((item, i) => (
                  <ListItem
                      key={i}
                      className={classes.listItem}
                      button
                      onClick={() => handleSelect(item, i)}
                  >
                      {item.type === "hotel" && <Hotel size="small" />}
                      {(item.type === "location" || !item.type) && (
                          <Local size="small" />
                      )}
                      {item.type === "airport" && <Air size="small" />}
                      <span className={classes.content}>
                          <Typography className={classes.name} type="body2semibold">
                              {item.name}
                          </Typography>
                          <Typography className={classes.adress} type="body2regular">
                              {" "}
                              {item.address}
                          </Typography>
                      </span>
                  </ListItem>
              ))}
                    </List>
                </div>
            </Box>
        </div>
    );
};

PesqincPackage.propTypes = {
    classes: PropTypes.object.isRequired,
    search: PropTypes.array,
    history: PropTypes.array,
    mostSearched: PropTypes.array,
};

export default withStyles(styles, { withTheme: true })(PesqincPackage);
