import React, { createContext, useCallback, useState, useContext, useEffect } from "react";

interface RenderStepsContextData {
    currentStep: string;
    renderNewStep(step: string | "NewRegister" | "ChangePassword"): void;
    previousStep: string;
    setPreviousStep(prev: string);
    toggleStepsWithModal: boolean;
    setToggleStepsWithModal(): void;
    hasCheckedValidationCode: boolean
}

const RenderStepsContext = createContext<RenderStepsContextData>({} as RenderStepsContextData);

const RenderStepsProvider: React.FC = ({ children }) => {
    const [currentStep, setCurrentStep] = useState("MainScreen");
    const [prevStep, setPrevStep] = useState("");
    const [toggleStepsWithModal, setStepsWithModal] = useState(false);
    const [hasCheckedValidationCode, setHasCheckedValidationCode] = useState(false)

    const renderNewStep = useCallback(step => {
        setCurrentStep(step);
    }, []);

    useEffect(() => {
        if(currentStep === "ValidationCode"){
            setHasCheckedValidationCode(true)
        }
    }, [currentStep])

    const setToggleStepsWithModal = useCallback(() => {
        setStepsWithModal(!toggleStepsWithModal)
    }, [toggleStepsWithModal])

    const setPreviousStep = useCallback(prev => {
        setPrevStep(prev);
    }, []);
    return (
        <RenderStepsContext.Provider value={
            {
                currentStep,
                previousStep: prevStep,
                renderNewStep,
                setPreviousStep,
                setToggleStepsWithModal,
                toggleStepsWithModal,
                hasCheckedValidationCode
            }}>
            {children}
        </RenderStepsContext.Provider>
    );
};

function useRenderSteps(): RenderStepsContextData {
    const context = useContext(RenderStepsContext);

    return context;
}

export { RenderStepsProvider, useRenderSteps };
